mapper.select:
SELECT name, price, descript, pict FROM items WHERE status
= 'ACT'
AND id = #{id}

UPDATE items
SET name = #{name},
price = #{price}, descript = #{descript}
WHERE id = #{id}

UPDATE items
SET status = 'DEL'
WHERE status = 'ACT' AND id = #{id}

UPDATE items
SET buyer = #{buyer}, purchase = 0 
WHERE id = #{id}

UPDATE items
SET purchase = 1, buyer = 'none'
WHERE id = #{id}

UPDATE items
SET purchase = 0, buyer = 'none'
WHERE id = #{id}
